mapper.select:
select m115.org_cd AS orgCd , m115.atfl_mng_no AS atflMngNo , m115.atfl_nm AS atflNm , m115.orgl_atfl_nm AS orglAtflNm , m115.atfl_path_nm AS atflPathNm from ohxzown.tbhxzm115 m115
join ohxzown.tbhxzm106 m106 on m115.org_cd = m106.org_cd
where 1=1 and m115.org_cd = #{orgCd} and m115.atfl_mng_no = #{atflMngNo}
