mapper.select:
select sprt.org_cd as orgCd, sprt.tpw_org_nm as tpwOrgNm, sprt.tpw_svc_ctt as tpwSvcCtt, sprt.tpw_svc_stt_dt as tpwSvcSttDt, sprt.tpw_svc_end_dt as tpwSvcEndDt, sprt.tpw_svc_nm as tpwSvcNm, sprt.tpw_svc_id as tpwSvcId, sprt.tpw_svc_typ_id as tpwSvcTypId, sprt.tpw_svc_typ_sno as tpwSvcTypSno, sprt.tpw_svc_typ_nm as tpwSvcTypNm, sprt.tpw_mbrs_typ_cd as tpwMbrsTypCd from ( select m201.org_cd, m009.tpw_org_nm, m201.tpw_svc_ctt, m201.tpw_svc_stt_dt, m201.tpw_svc_end_dt, m201.tpw_svc_nm, m201.tpw_svc_id, coalesce(m202.tpw_svc_typ_id,'0') as tpw_svc_typ_id, coalesce(m202.tpw_svc_typ_sno,'0') as tpw_svc_typ_sno, m202.tpw_svc_typ_nm, m202.tpw_mbrs_typ_cd from ohxzown.tbhxzm201 m201 join ohxzown.tbhxzm009 m009 on m201.org_cd = m009.org_cd left join ohxzown.tbhxzm202 m202 on m201.tpw_svc_id = m202.tpw_svc_id left join ohxzown.tbhxzm103 m103 on m202.tpw_mbrs_typ_cd = m103.tpw_mbrs_typ_cd where m201.use_yn = 'Y'
and m201.org_cd = #{orgCd}
and m201.tpw_svc_nm like concat('%', #{req.tpwSvcNm}, '%')
and (m201.tpw_svc_stt_dt between #{req.sttDt} and #{req.endDt} or m201.tpw_svc_end_dt between #{req.sttDt} and #{req.endDt})
and m103.tpw_mbrs_typ_cd_nm like concat('%', #{req.tpwMbrsTypCdNm}, '%')
and m201.tpw_svc_id = #{req.tpwSvcId}
and m202.tpw_svc_typ_id = #{req.tpwSvcTypId}
) sprt order by sprt.${req.sort} ${req.dir} limit #{req.size} offset #{req.page} * #{req.size}

select count(1) from ohxzown.tbhxzm201 m201 join ohxzown.tbhxzm009 m009 on m201.org_cd = m009.org_cd left join ohxzown.tbhxzm202 m202 on m201.tpw_svc_id = m202.tpw_svc_id left join ohxzown.tbhxzm103 zm103 on m202.tpw_mbrs_typ_cd = zm103.tpw_mbrs_typ_cd where 1 = 1 and m201.use_yn = 'Y'
and m201.org_cd = #{orgCd}
and m201.org_cd = #{orgCd}
and m009.tpw_org_nm like concat('%', #{req.orgNm}, '%')
and m201.tpw_svc_nm like concat('%', #{req.svcNm}, '%')
and m201.tpw_svc_stt_dt between #{req.sttDt} and #{req.endDt} and m201.tpw_svc_end_dt between #{req.sttDt} and #{req.endDt}
and zm103.tpw_mbrs_typ_cd_nm like concat('%', #{req.tpwMbrsTypCdNm}, '%')
and m201.tpw_svc_id = #{req.tpwSvcId}
and m202.tpw_svc_typ_id = #{req.tpwSvcTypId}
and m201.tpw_svc_id = m202.tpw_svc_id

mapper.insert:
insert into ohxzown.tbhxzm201 (
org_cd,
tpw_svc_id,
tpw_svc_nm,
tpw_svc_stt_dt,
tpw_svc_end_dt,
tpw_svc_ctt,
krn_chec_yn,
use_yn,
acng_trdp_no,
bnk_trn_ctt,
tpw_svc_scrn_path_nm,
rgsr_id,
rgt_dtm,
updr_id,
upd_dtm
)
select
/* 1. org_cd */
#{orgCd},

/* 2. tpw_svc_id 계산 (규칙 유지) */
(
select
substr(#{orgCd}, 1, 4) || lpad(cast(coalesce(cast(max(substr(tpw_svc_id, 5, 3)) as integer), 0) + 1 as text), 3, '0')
from ohxzown.tbhxzm201
where tpw_svc_id like substr(#{orgCd}, 1, 4) || '%'
and org_cd = #{orgCd}
),

/* 3. tpw_svc_nm */
#{tpwSvcNm},

/* 4. tpw_svc_stt_dt */
#{tpwSvcSttDt},

/* 5. tpw_svc_end_dt */
#{tpwSvcEndDt},

/* 6. tpw_svc_ctt */
#{tpwSvcCtt},

/* 7. krn_chec_yn */
#{krnChecYn},

/* 8. use_yn */
#{useYn},

/* 9. acng_trdp_no */
#{acngTrdpNo},

/* 10. bnk_trn_ctt */
#{bnkTrnCtt},


/* 11. tpw_svc_scrn_path_nm */
#{tpwSvcScrnPathNm},

/* 12. rgsr_id (상수 처리) */
'system',

/* 13. rgt_dtm (함수 처리) */
to_char(now(), 'YYYYMMDDHH24MISS'),

/* 14. updr_id (상수 처리) */
'system',

/* 15. upd_dtm (함수 처리) */
to_char(now(), 'YYYYMMDDHH24MISS')

mapper.update:
update ohxzown.tbhxzm202
set use_yn = 'N'
where tpw_svc_id = #{tpwSvcId}
and tpw_svc_typ_id = #{tpwSvcTypId}
and tpw_svc_typ_sno = #{tpwSvcTypSno}

mapper.select:
select *
from ohxzown.tbhxzm202
where tpw_svc_id = #{tpwSvcId}
and tpw_svc_typ_id = #{tpwSvcTypId}
and tpw_svc_typ_sno = #{tpwSvcTypSno}
